INSERT INTO toc VALUES(1, 'آداب العمره');
INSERT INTO toc VALUES(2, 'الاحرام و أدابه');
INSERT INTO toc VALUES(3, 'الطواف بالكعبة المشرفة');
INSERT INTO toc VALUES(4, 'السعي بين الصفا والمروة');
INSERT INTO toc VALUES(5, 'الحلق او التقصير');
INSERT INTO toc VALUES(6, 'زيارة المدينة المنورة');
INSERT INTO prayer VALUES(1, 'الشوط الاول', 'سبحان الله والحمد لله ولا اله الا الله والله اكبر ولا حول ولا قوة الا بالله العلي العظيم ,والصلاه والسلام علي رسول الله صلي الله عليه وسلم ,اللهم ايمانا بك وتصديقا لكتابك ووفاء بعهدك واتباعا لسنة نبيك وحبيبك محمد صلي الله عليه وسلم.                                      	
INSERT INTO prayer VALUES(2, 'الشوط الثاني', 'اللهم هذا البيت بيتك, والحرم حرمك ,والامن امنك ,والعبد عبدك وانا عبدك وابن عبدك ,وهذا مقام العائذ بك من النار فحرم لحومنا وبشرتنا علي النار ,اللهم حبب الينا الايمان وزينه في قلوبنا وكره الينا الكفر والفسوق والعصيان واجعلنا من الراشدين ,اللهم قني عذابك يوم تبعث عبادك الله ام ارزقني الجنة بغير حساب ,ربنا آتنا في الدنيا حسنة وفي الاخرة حسنة وقنا عذاب النار وادخلنا الجنة مع الابرار يا عزيز يا غفار يا رب العالمين
INSERT INTO prayer VALUES(3, 'الشوط الثالث', '"اللهم اني اعوذ بك من الشك والشرك والشقاق والنفاق وسوء الاخلاق وسوء المنظر والمنقلب في المال والاهل والولد اللهم اني اسالك رضاك والجنه واعوذ بك من سخطك والنار الله ام اني اعوذ بك من فتنه القبر واعوذ بك من فتنه المحيا والممات ربنا اتنا في الدنيا حسنه وفي الاخره حسنه وقنا عذاب الناروادخلنا الجنه مع الابرار يا عزيز يا غفار يارب العالمين" 
INSERT INTO prayer VALUES(4, ' الشوط الرابع', '"اللهم اجعله حجا مبرورا ,وسعيا مشكورا ,وذنبا مغفورا وعملا صالحا مقبولا وتجارة لن تبور يا عالم ما في الصدور ,اخرجني يا الله من الظلمات الي النور ,اللهم اني اسالك موجبات رحمتك وعزائم مغفرتك السلامة من كل اثم والغنيمة من كل بر والفوز بالجنة والنجاة من النار ,ربي اقنعني بما رزقتني وبارك لي فيما اعطيتني واخلف علي كل غائبه لي منك بخير. ربنا آتنا في الدنيا حسنة وفي الاخرة حسنة وقنا عذاب النار وادخلنا الجنة مع الابرار يا عزيز يا غفار يا رب العالمين
INSERT INTO paragraph VALUES(1, ' ينبغي للعبد أن يقوم بشعائر الحج على سبيل التعظيم والإجلال والمحبة والخضوع لله رب العالمين ,فيؤديها بسكينة ووقار واتباع لرسول الله صلى الله عليه وسلم.', 1,1);
INSERT INTO paragraph VALUES(2, 'ينبغي أن يشغل هذه المشاعر العظيمة بالذكر والتكبير والتسبيح والتحميد والاستغفار لأنه في عبادة من حين أن يشرع في الإحرام حتى يحل منه, فليس الحج نزهة للهو واللعب يتمتع به الإنسان كما شاء من غير حد.', 2, 1);
INSERT INTO paragraph VALUES(3, 'الحفاظ على ما أوجبه الله عليه من الصلاة جماعة في أوقاتها.', 3, 1);
INSERT INTO paragraph VALUES(4, ' الحرص على نفع المسلمين والإحسان إليهم بالإرشاد والمعونة عند الحاجة ,والأمر بالمعروف والنهي عن المنكر, وأن يرحم ضعيفهم خصوصا في مواضع الرحمة كمواضع الزحام ونحوها فإن رحمة الخلق جالبة لرحمة الخالق وإنما يرحم الله من عباده الرحماء. ', 4, 1);
INSERT INTO paragraph VALUES(5, 'تجنب الرفث والفسوق والعصيان والجدال لغير نصرة الحق ، أما الجدال من أجل نصرة الحق فهذا واجب في موضعه .', 5, 1);
INSERT INTO paragraph VALUES(6, 'تجنب الاعتداء على الخلق وإيذاءهم ,و تجنب الغيبة والنميمة والسب والشتم والضرب والنظر إلى النساء الأجانب.', 6, 1);
INSERT INTO paragraph VALUES(7, 'معناه ان ينوي المسلم اداء العمرة  تقربا الى الله تعالى , ورغبة في ثوابه , وخوفا من عقابه ويستحب للمحرم ان يبين ما يحرم به وان ينطق به فيقول : اللهم اني نويت العمرة فيسرها لي وتقبلها مني .واذا لم يتلفظ بذلك فلا بأس لأن النية محلها القلب .', 1, 2);
INSERT INTO paragraph VALUES(8, 'ولا يصح لمن يريد ان يؤدي العمرة ان يتجاوزالميقات (وهوالمكان الذي حددته شريعة الاسلام للاحرام منه) الا وهو محرم واذا نوى من يؤدي العمرة الذهاب الى المدينة المنورة اولا ، بقى بملابسه العادية ، واستمر على حياته العادية . فإذا ما استقر بالمدينة المنورة واراد الذهاب الى المسجد النبوي الشريف فيستحب له قبل الذهاب ان يغتسل ، ويلبس احسن ثيابه فإذا ما اراد السفر الى مكة المكرمة، لبس ملابس الاحرام واحرم من ميقات اهل المدينة وهو مكان يسمى بذي الحليفة ويطلق عليه الان ابيار علي.', 2, 2);
INSERT INTO paragraph VALUES(9, 'الاستعداد للاحرام عن طريق النظافة و الاغتسال وهو افضل من الوضوء وتقليم الاظافر وقص الشارب و ازالة الزوائد التي في امكنة اخرى من البدن. والتجرد من الثياب العادية وارتداء ثوب الاحرام وهو للرجل: رداء يلف به النصف الاعلى من البدن دون الرأس وإزار يلف به النصف الاسفل منه ,أما للمرأة تبقى علي ملابسها المحتشمة السابغة التي لا تصف ولا تشف ولا يبدو منها وهي محرمة سوي الوجه والكفين الا انه يجوز لها ان تستر وجهها بشي رقيق عند مرور الرجال', 3, 2);
INSERT INTO paragraph VALUES(10, 'صلاة ركعتين ينوي بهما سنه الاحرام ، ومن المستحب ان يقرا في الركعه الاولي بعد الفاتحة سورة الكافرون ويقرا في الركعة الثانية بعد الفاتحة صورة الاخلاص.', 4, 2);
INSERT INTO paragraph VALUES(11, 'الاشياء التي يباح للمحرم ان يفعلها:الاغتسال وتغير الرداء والازار و مداواه الجراح و شد الهيمان – اي الحزام – في وسط المحرم ليحفظ نقوده او نقود غيره . استعمال المظله وما يشبهها للوقايه من شده حراره الشمس', 5, 2);
INSERT INTO paragraph VALUES(12, 'الاشياء المحظوره علي المحرم فعلها:حلق المحرم رأسه اوغطائها بملاصق كعمامه او ما يشبهها ,او لبس ثوبا مخيطا ,او مس طيبا, أوقتل صيد البر او التعرض له باذي او التسبب في صيده ,كذلك يحرم الخصام والجدال مع الرفقاء باسلوب يودي الي التنازع والتقاطع والتدابر ,و الجماع ودواعيه, و ان يعقد المحرم لنفسه او لغيره عقد زواج', 6, 2);
INSERT INTO paragraph VALUES(13, 'يبدأ في الطواف بنشاط واخلاص محاذيا الحجر الاسود فإذا امكنه تقبيله خلال طوافه قبله ,واذا لم يتمكن من ذلك اشار اليه وقال بسم الله الله اكبر ,يستحب للطائف ان يسرع في الاشواط الثلاثة الاولى ويمشي مشيا عاديا في الاشواط الاربعة الباقية, وتكون نهايته للطواف من المكان الذي بدأ منه وهو محاذاة الحجر الاسود ,كما يستحب له ان يستلم الركن اليماني في كل شوط من الاشواط السبعة ,وايضا يستحب له ان يكثر من الذكر والدعاء ويتخير منهما ما ينشرح له صدره ,كأن يقول اذا اخذ في الطواف " سبحان الله والحمد لله ولا اله الا الله والله اكبر ولا حول ولا قوة الا بالله العلي العظيم " وكان يقول اذا ما انتهى الى الركن اليماني :" ربنا آتنا في الدنيا حسنة والاخرة حسنة وقنا عذاب النار "', 1, 3);
INSERT INTO paragraph VALUES(14, 'ومن سنن الطواف : صلاة ركعتين بعده عند مقام ابراهيم ان امكن ذلك ,فإن لم يتمكن صلاهما في اي مكان من المسجد الحرام ,كذلك يستحب للمعتمر بعد فراغه من الطواف ان يشرب من ماء زمزم ,فقد ثبت في الصحيحين ان رسول الله صلى الله عليه وسلم شرب من ماء زمزم وانه قال : " انها مباركة " ويسن ان ينوي الشارب عند شربه من ماء زمزم الدعاء بالشفاء والزيادة من كل خير ففي الحديث الشريف " ماء زمزم لما شرب له ".', 2, 3);
INSERT INTO paragraph VALUES(15, 'الصفا والمروة وهما : المكانان المرتفعان في بداية السعي ونهايته و من اهم شروط السعي بينهما: ان يكون هذا السعي مسبوقا بالاحرام فلو سعى ثم احرم لم يصح السعي . وان يكون هذا السعي بعد الطواف ، لأن الرسول صلى الله عليه وسلم فعل ذلك', 1, 4);
INSERT INTO paragraph VALUES(16, 'السعي  سبعة اشواط وان يبدأ الساعي بالصفا ويختتم بالمروة . ومن اهم سنن السعي : الموالاة فيه ، فلو فصل بين الاشواط بفاصل يسير وبدون عذر ، يكون قد اساء وخالف السنة كذلك يسن للساعي ان يكون على طهارة ، فلو خالف ذلك كان مخالفا للسنة . وايضا يسن للساعي ان يصعد على الصفا والمروة كلما وصل اليهما . فقد ثبت في الحديث الصحيح ان الرسول صلى الله عليه وسلم " صعد على كل منهما خلال سعيه حتى رأى المسجد الحرام .', 2, 4);
INSERT INTO paragraph VALUES(17, 'يستحب للساعي ان كان رجلا أن يهرول بين الملين الاخضرين اما المرأه فتمشي مشيا عاديا ويجوز السعي عن طريق المشي او الركوب والمشي أفضل للقادر عليه وعلي الساعي بين الصفا و المروه ان يكثر من ذكر الله ومن التضرع اليه بالدعاء الذي ينشرح له صدره وقد روي عن النبي صلي الله عليه وسلم انه كان يقول خلال سعيه : "رب اغفر وارحم واهدني السبيل الاقوم انك انت الاعز الاكرم ".', 3, 4);
INSERT INTO paragraph VALUES(18, 'المقصود بالحلق: ازاله شعر الراس والمقصود بالتقصير أن يقص المعتمر من شعره رأسه عددا من الشعرات والحلق هو الافضل. وبالنسبه للمرأه فليس عليها الا ان تقص بعض الشعرات من اطراف شعرها ففي الحديث الشريف :" ليس علي النساء الحلق انما علي النساء التقصير "', 1, 5);
INSERT INTO paragraph VALUES(19, 'قبل الذهاب للمسجد الشريف يستحب للانسان ان يغتسل ويلبس ثيابه ويمس ما يستطيعه من الطيب ويستحضر في قلبه شرف المدينه المنوره وفضلها ... ويكثر من الصلاه علي النبي صلي الله عليه وسلم.', 1, 6);
INSERT INTO paragraph VALUES(20, 'اذا ما وصل المسجد الشريف دخل برجله اليمني وقال"بسم الله، اللهم صلي علي محمد وعلي آله وصحبه وسلم ، اللهم اغفر لي ذنوبي وافتح لي ابواب رحمتك"', 2, 6);
INSERT INTO paragraph VALUES(21, 'ثم يتجه بعد ذلك الي الروضه الشريفه فاذا وجد مكانا صلي فيه ركعتين تحيه المسجد في ادب وخشوع واخلاص واذا لم يجد مكانا صلي في ايه بقعه من المسجد الشريف.', 3, 6);
INSERT INTO paragraph VALUES(22, 'ثم يتجه بعد الصلاه الي القبر الشريف  فيقف امامه ويسلم علي صاحبه صلي الله عليه وسلم السلام عليك يا رسول الله السلام عليك يا نبي الله السلام عليك يا خير خلق الله، السلام عليك يا حبيب الله ، السلام عليك يا خير المرسلين ، السلام عليك يا رسول رب العالمين ثم يدعو الله – تعالي – بما يشاء من دعوات خاشعات جامعات للخير والتقي والعفاف.', 4, 6);
INSERT INTO paragraph VALUES(23, 'ثم يتاخر نحو ذراع الي الجهه اليمني فيسلم علي ابي بكر الصديق ، ثم يتاخر نحو ذراع فيسلم علي عمر الفاروق – رضي الله عنهما – ثم يستقبل القبله فيدعو الله – تعالي – بجوامع الدعاء لنفسه ولسائر المسلمين.', 5, 6);
INSERT INTO paragraph VALUES(24, 'وعلي الزائر ان يلتزم الخشوع والوقار وان يخفض من صوته وان يتجنب ما ينتافا مع الاداب الاسلام من تمسح بالباب او تقبيله.', 6, 6);
INSERT INTO paragraph VALUES(25, 'وعلي الزئر خلال بقائه بالمدينه المنوره ان يزور مسجد قباء فقد كان صلي الله عليه وسلم يتردد عليه كل يوم سبت ويصلي فيه ما شاء له ان يصلي وان يزور البقيع وشهداء احد فاذا ما اراد الانصراف من المدينه كان اخر عهده بها صلاه ركعتين في المسجد النبوي الشريف ، نسال الله – تعالي – أن يرزقنا شفاعته صلي الله عليه وسلم.', 7, 6);
